import React from 'react';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      passwordMinLengthBtnDisabled: true,
    };
    this.handleChange = this.handleChange.bind(this);
    this.isEmailPasswordValid = this.isEmailPasswordValid.bind(this);
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
    this.isEmailPasswordValid();
  }

  // Referência a thread no slack do tryber Michael Caxias
  isEmailPasswordValid() {
    const { email, password } = this.state;
    const regexEmail = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;
    const passwordMinLength = 6;

    if (regexEmail.test(email) && passwordMinLength <= password.length) {
      this.setState({
        passwordMinLengthBtnDisabled: false,
      });
    } else {
      this.setState({
        passwordMinLengthBtnDisabled: true,
      });
    }
  }

  render() {
    const { email, password, passwordMinLengthBtnDisabled } = this.state;
    return (
      <div>
        <label htmlFor="login">
          Login
          <input
            type="text"
            data-testid="email-input"
            name="email"
            value={ email }
            onChange={ this.handleChange }
            id="login"
            placeholder="email"
          />
        </label>
        <label htmlFor="Senha">
          Senha
          <input
            type="text"
            data-testid="password-input"
            name="password"
            value={ password }
            onChange={ this.handleChange }
            id="Senha"
            placeholder="Senha"
          />
        </label>
        <Link
          to="/carteira"
        >
          <button
            type="button"
            disabled={ !passwordMinLengthBtnDisabled }
          >
            Entrar
          </button>
        </Link>
      </div>
    );
  }
}
export default Login;
