import { REQUEST_WALLET } from '../actions';

const INNITIAL_STATE = {
    wallet: {
        currencies: [],
        expenses: []
        }
};

const user = (state = INNITIAL_STATE, action) => {
  switch (action.type) {
  case REQUEST_WALLET:
    return { ...state, user: action.login };
  default:
    return state;
  }
};

export default user;
