import React from 'react';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      passwordMinLengthBtnDisabled: true,
    };
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
    this.isEmailPasswordValid();
  }

  // Referência a thread no slack do tryber Michael Caxias
  isEmailValid() {
    const { email } = this.state;
    const regexEmail = /^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/;
    return regexEmail.test(email);
  }

  render() {
    const { email, password } = this.state;
    return (
      <div>
        <label htmlFor="login">
          Login
          <input
            type="text"
            data-testid="email-input"
            name="email"
            value={ email }
            onChange={ this.handleChange }
            id="login"
            placeholder="email"
          />
        </label>
        <label htmlFor="Senha">
          Senha
          <input
            type="text"
            data-testid="password-input"
            name="password"
            value={ password }
            onChange={ this.handleChange }
            id="Senha"
            placeholder="Senha"
          />
        </label>
        <button
          type="button"
          onClick={ () => {} }
          disabled={ !this.isEmailValid() || !this.isPasswordValid }
        >
          Entrar

        </button>
      </div>
    );
  }
}

export default Login;
