import React from 'react';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
      disabled: 6,
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleDisabled = this.handleDisabled.bind(this);
    this.isEmailValid = this.isEmailValid.bind;
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value }, () => this.handleDisabled);
  }

  // Referência a thread no slack do tryber Michael Caxias

  validateEmail() {
    const { email } = this.state;
    const validEmail = /\S+@\S+\.\S+/;
    return validEmail.test(email);
  }

  validatePassword() {
    const { password, disabled } = this.state;
    if (password.length >= disabled) return true;
    return false;
  }

  render() {
    const { email, password, disabled } = this.state;
    return (
      <div>
        <label htmlFor="login">
          Login
          <input
            type="text"
            data-testid="email-input"
            name="email"
            value={ email }
            onChange={ this.handleChange }
            id="login"
            placeholder="email"
          />
        </label>
        <label htmlFor="Senha">
          Senha
          <input
            type="text"
            data-testid="password-input"
            name="password"
            value={ password }
            onChange={ this.handleChange }
            id="Senha"
            placeholder="Senha"
          />
        </label>
        <Link
          to="/carteira"
        >
          <button
            type="button"
            disabled={ disabled }
          >
            Entrar
          </button>
        </Link>
      </div>
    );
  }
}

export default Login;
