import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { fetchCurrencyAPI, expensesAPI } from '../actions/index';

class WalletForms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      description: '',
      currency: '',
      method: '',
      tag: '',
    };

    this.renderValue = this.renderValue.bind(this);
    this.renderDescription = this.renderDescription.bind(this);
    this.renderCurrency = this.renderCurrency.bind(this);
    this.renderPayment = this.renderPayment.bind(this);
    this.renderTag = this.renderTag.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    const { getWalletData } = this.props;
    getWalletData();
  }

  handleChange({ target: { name, value } }) {
    this.setState({ [name]: value });
  }

  renderValue() {
    const { value } = this.state;
    return (
      <label htmlFor="expenses">
        Valor
        <input
          type="number"
          name="value"
          id="expenses"
          value={ value }
          onChange={ this.handleChange }
          placeholder="Expenses"
        />
      </label>

    );
  }

  renderDescription() {
    const { description } = this.state;
    return (
      <label htmlFor="Description">
        Descrição
        <input
          type="text"
          name="description"
          id="Description"
          value={ description }
          onChange={ this.handleChange }
          placeholder="Description"
        />
      </label>
    );
  }

  renderCurrency() {
    const { getDataCurrencies } = this.props;
    const { currency } = this.state;
    return (
      <label htmlFor="currency">
        Moeda
        <select
          name="currency"
          id="currency"
          value={ currency }
          onChange={ this.handleChange }
        >
          { getDataCurrencies.map((currencies, index) => (
            <option
              key={ index }
              value={ currencies }
            >
              { currencies }
            </option>
          )) }
        </select>
      </label>
    );
  }

  renderPayment() {
    const { method } = this.state;
    return (
      <label htmlFor="method">
        Método de Pagamento
        <select
          type="number"
          name="method"
          id="method"
          value={ method }
          onChange={ this.handleChange }
          placeholder="Payment"
        >
          <option value="Dinheiro">Dinheiro</option>
          <option value="Cartão de Crédito">Cartão de Crédito</option>
          <option value="Cartão de Débito">Cartão de Débito</option>
        </select>
      </label>
    );
  }

  renderTag() {
    const { tag } = this.state;
    return (
      <label htmlFor="Tag">
        Tag
        <select
          type="number"
          name="tag"
          id="Tag"
          value={ tag }
          onChange={ this.handleChange }
          placeholder="Tag"
        >
          <option value="Alimentação">Alimentação</option>
          <option value="Lazer">Lazer</option>
          <option value="Trabalho">Trabalho</option>
          <option value="Transporte">Transporte</option>
          <option value="Saúde">Saúde</option>
        </select>
      </label>
    );
  }

  render() {
    return (
      <form>
        { this.renderValue() }
        { this.renderDescription() }
        { this.renderCurrency() }
        { this.renderPayment() }
        { this.renderTag() }
        <button type="submit">Adicionar despesa</button>
      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  getWalletData: (state) => dispatch(fetchCurrencyAPI(state)),
});

const mapStateToProps = (state) => ({
  getDataCurrencies: state.wallet.currencies,
});

WalletForms.propTypes = {
  getWalletData: PropTypes.func.isRequired,
  getDataCurrencies: PropTypes.shape().isRequired,
};

export default connect(mapStateToProps, mapDispatchToProps)(WalletForms);
